        this(name, 1, 2, 1, 1, 1, 1, 1);
    }

    public Hero(String name, int str, int con, int aur,
                int wis, int dex, int agi, int cha) {

        super(name, dex, str, con * 10, 1);

        skills = new HashMap();
        i = new Inventory(str * 10);

        this.str = str;
        this.con = con;
        this.aur = aur;
        this.wis = wis;
        this.dex = dex;
        this.agi = agi;
        this.cha = cha;

