        wornArmor.wear(a);
        removeItem(a, 1, silent);

        if (!silent) {
            Control.infoIcon("You wear " + a, "Wearing Armor", a);
        }

        if (propertyChangeSupport != null) {
            propertyChangeSupport
                .firePropertyChange(PROPERTY_AC, oldAc, getAc());
        }
